export const GrenadeIcon = ({
  nade,
  ...props
}: { nade: number } & IconProps) => {
  switch (nade) {
    case Grenade.Flashbang:
      return <FlashbangIcon {...props} />;
    case Grenade.HE:
      return <HEIcon {...props} />;
    case Grenade.Smoke:
      return <SmokeIcon {...props} />;
    case Grenade.Molotov:
      return <MolotovIcon {...props} />;
    case Grenade.Decoy:
      return <DecoyIcon {...props} />;
    case Grenade.Incendiary:
      return <IncendiaryIcon {...props} />;
    default:
      return <></>;
  }
};

